import React from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useFormik } from 'formik';
import { useState } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom'
import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';
const Login = () => {

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')

    const navigate = useNavigate()

    const procceedLogin = (e) => {
        e.preventDefault();
        if (validate()) {
            // fetch("https://reqres.in/api/login").then((res) => {
            //     console.log(res);
            //     return res.json();
            // }).then((resp) => {
            //     if (Object.keys(resp).length === 0) {
            //         alert('Enter username')
            //     }
            //     else {
            //         if (resp.status === 200) {
            //             navigate('/')
            //             console.log('success');
            //         } else {
            //             alert('Err pass')
            //         }
            //     }
            // }).catch((err) => {
            //     alert('Fail')
            // })
            try {
                const resData =  axios.post(`${API}auth/login`, values)
                console.log(resData);
                alert('Đăng nhập thành công')

                if (resData.status === 200) {
                    navigate('/')
                    storageService.set("accessToken", resData.data.jwt)
                    storageService.set('isLogin', true)
                    storageService.set('username', resData.data.username)
                    storageService.set('id', resData.data.id)
                    // storageService.set('fullName', resData.data.fullName)
                    // storageService.set('email', resData.data.email)
                    // storageService.set('phoneNumber', resData.data.phoneNumber)
                }
            } catch (err) {
                console.log(err)
                alert('Fail')
            }
    }
}
const validate = () => {
    let result = true;
    if (username === '' || username === null) {
        result = false;
        alert('Enter username')
    }
    if (password === '' || password === null) {
        result = false;
        alert('Enter password')
    }
    return result;
}
return (
    <div className=''>
        <form onSubmit={procceedLogin} className='container'>
            <div className='card'>
                <div className='card-header'>
                    <h2>Login</h2>
                </div>
                <div className='card-body'>
                    <div className='form-group'>
                        <label>Username <span className='errmsg'></span> </label>
                        <input value={username} onChange={e => setUsername(e.target.value)} className='' type="text" />
                    </div>
                    <div className='form-group'>
                        <label>Password<span className='errmsg'></span> </label>
                        <input value={password} onChange={e => setPassword(e.target.value)} className='' type="text" />
                    </div>
                </div>
                <div className='card-footer'>
                    <button onClick={procceedLogin} type='submit'>Login</button>
                    <Link to='/signup'>
                        <button>New user</button>
                    </Link>
                </div>
            </div>
        </form >
    </div >
)
}

export default Login
